sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    // rightnow stores the current time
    var rightnow = new GlideDateTime();
    // Create a GlideDateTime object for the When needed date
    var whenNeeded = new GlideDateTime(current.u_when_needed);

    var today = rightnow.getLocalDate();
    var wnDate = whenNeeded.getLocalDate();
    var daysDiff = wnDate.compareTo(today);

	// testing debug
	var msg = "Today: " + today + "\n" + "wnDate: " + wnDate;
	gs.info("Info log from NeedIt");
	gs.warn("warn log from NeedIt");
	gs.debug("debug log from NeedIt");
	gs.error("error log from NeedIt");

    // If the When needed date is before rightnow, do not write the record to the database
    // Output an error message to the screen
    if (daysDiff <= 0) {
        gs.addErrorMessage("When needed date cannot be in the past or in the current day.  Your request has not been saved to the database.");
        current.setAbortAction(true);
    }

})(current, previous);
