sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if (oldValue != newValue) {
		var dgs = new Date();
		let yy = dgs.getFullYear();
		let MM = dgs.getMonth();
		if (MM <10)
			MM = '0' + MM;
		let dd = dgs.getDate();
		if (dd < 10)
			dd = '0' + dd;
		let hh = dgs.getHours();
		if (hh < 10)
			hh = '0' + hh;
		let mm = dgs.getMinutes();
		if (mm < 10)
			mm = '0' + mm;
		let ss = dgs.getSeconds();
		if (ss < 10)
			ss = '0' + ss;
		let today = '' + MM + '-' + dd + '-' + yy + ' ' + hh + ':' + mm + ':' + ss;
		//console.log(today);
		g_form.setValue('last_modified',today);
	}

   //Type appropriate comment here, and begin script below

}
